// This function prepares the MongoDB aggregation pipeline parameters
// based on the request type and filters passed in the msg payload

// Get the payload and debug
const payload = msg.payload || {};
node.warn("Preparing aggregate parameters with payload: " + JSON.stringify(payload));

// Default parameters
let match = { status: { $ne: "completed" } }; // Default to show pending todos
let sort = { priority: 1, created_at: -1 }; // Default sort by priority then date

// Get project filter from global context, if it exists
const projectFilter = global.get("projectFilter") || "all";
if (projectFilter !== "all")
{
    match.project = projectFilter;
    node.warn("Filtering by project: " + projectFilter);
}

// Check if a date is specified in the payload
if (payload.date && payload.date !== 'all')
{
    // Parse the date string to Date object
    const date = new Date(payload.date);
    const dateStr = date.toISOString().split('T')[0]; // YYYY-MM-DD format

    // Create a range for the entire day
    const startDate = new Date(dateStr + "T00:00:00.000Z");
    const endDate = new Date(dateStr + "T23:59:59.999Z");

    // Add date filter to match stage
    match.due_date = {
        $gte: Math.floor(startDate.getTime() / 1000),
        $lte: Math.floor(endDate.getTime() / 1000)
    };

    node.warn("Filtering by date: " + dateStr);
}

// Prepare the aggregation pipeline
const pipeline = [
    // Match stage to filter todos
    { $match: match },

    // Sort todos by priority and creation date
    { $sort: sort },

    // Limit to most important 8 todos per day
    { $limit: 200 },

    // Project to add scheduling metadata
    {
        $project: {
            _id: 0,
            id: "$id",
            todo_id: "$id",
            description: 1,
            priority: 1,
            status: 1,
            created_at: 1,
            // Estimate duration based on priority
            duration_minutes: {
                $switch: {
                    branches: [
                        { case: { $eq: ["$priority", "high"] }, then: 60 },
                        { case: { $eq: ["$priority", "medium"] }, then: 45 },
                        { case: { $eq: ["$priority", "initial"] }, then: 45 }
                    ],
                    default: 30  // low priority gets 30 minutes
                }
            },
            project: 1,
            due_date: 1,
            notes: 1
        }
    }
];

// Set the query parameters for MongoDB
// IMPORTANT: For MongoDB4 node, payload must be an array containing the pipeline
msg.payload = [pipeline];
msg.collection = "todos";
msg.operation = "aggregate";

node.warn("Aggregate pipeline prepared with " + pipeline.length + " stages");
return msg;

// This function sets the project filter in global context
// and initiates a refresh of the todo list and suggestions

// Extract project from payload
const projectFilter = msg.payload.project || "all";

// Store in global context for use by other nodes
global.set("projectFilter", projectFilter);

// Log what we're doing
node.warn("Setting project filter to: " + projectFilter);

// Return two messages: one to refresh todos and one to refresh suggestions
return [
    {
        topic: "todo/action/refresh_suggestions",
        payload: 1
    },
    {
        topic: "todo/action/refresh_todos",
        payload: 1
    },
    {
        topic: "todo/action/refresh_todos",
        payload: 1
    }
];

// Get the todo ID from the payload
const todoId = msg.payload.id;

if (!todoId)
{
    return { payload: { status: "error", message: "No todo ID provided" } };
}

// Format according to mongodb4 node documentation
// msg.payload must be an array of arguments for the driver operation
// For updateOne: first arg is query, second is update, third is options

// Prepare arguments as documented format
const query = { id: todoId };
const update = {
    $set: {
        status: "completed",
        completed_at: Math.floor(Date.now() / 1000)
    }
};
const options = { upsert: false };

// Set operation parameters according to docs
msg.payload = [query, update, options];
msg.collection = "todos";
msg.operation = "updateOne";

return msg;

// Get the todo ID from the payload
const todoId = msg.payload.id;

if (!todoId) {
    return { payload: { status: "error", message: "No todo ID provided" } };
}

// Prepare MongoDB find query
return {
    payload: {
        // Query for MongoDB4 node format
        filter: { id: todoId }
    },
    collection: "todos"
};

// Get the todo ID from the payload
const todoId = msg.payload.id;

if (!todoId)
{
    return { payload: { status: "error", message: "No todo ID provided" } };
}

// Keep track of whether this is a direct edit request (from Copy ID)
// This flag will be used to prevent refreshing the todo list
const noRefresh = msg.noRefresh === true;
const directEdit = msg.directEdit === true;

// Format according to mongodb4 node documentation
// msg.payload must be an array of arguments for the driver operation
// For findOne: first arg is query, second is options

// Prepare arguments as documented format
const query = { id: todoId };
const options = {}; // No specific options needed for this operation

// Set operation parameters according to docs
msg.payload = [query, options];
msg.collection = "todos";
msg.operation = "findOne";
msg.topic = "todo/action/edit"; // Keep the topic for routing

// Preserve the flags for use in downstream nodes
msg.noRefresh = noRefresh;
msg.directEdit = directEdit;

// Debug output
node.warn("Finding todo to edit with ID: " + todoId + ", noRefresh: " + noRefresh);

return msg;

// Get the todo ID and field update from the payload
const todoId = msg.payload.id;
const field = msg.payload.field;
const value = msg.payload.value;

if (!todoId)
{
    return { payload: { status: "error", message: "No todo ID provided" } };
}

if (!field)
{
    return { payload: { status: "error", message: "No field specified for update" } };
}

// Create update object with stringified ID to ensure it's treated as a string
const updateObj = {};
updateObj[field] = value;
updateObj.last_modified = Math.floor(Date.now() / 1000);

// If marking as completed, add completed_at timestamp
if (field === 'status' && value === 'completed')
{
    updateObj.completed_at = Math.floor(Date.now() / 1000);
}

// Format according to mongodb4 node documentation
// msg.payload must be an array of arguments for the driver operation
// For updateOne: first arg is query, second is update, third is options

// Prepare arguments as documented format
const query = { id: String(todoId) };
const update = { $set: updateObj };
const options = { upsert: false };

// Set operation parameters according to docs
msg.payload = [query, update, options];
msg.collection = "todos";
msg.operation = "updateOne";

return msg;

// Function to handle multiple field updates for a todo item
// This function receives a todo ID and an object with multiple field updates
// and processes them in a single MongoDB update operation

// Get the todo ID and updates from the payload
const todoId = msg.payload.id;
const updates = msg.payload.updates || {};

// Log the received updates for debugging
node.warn("Processing update_multiple for todo: " + todoId);
node.warn("Updates: " + JSON.stringify(updates));

if (!todoId) {
    return {
        payload: {
            status: "error",
            message: "No todo ID provided"
        },
        topic: "todo/dashboard/editor"
    };
}

if (Object.keys(updates).length === 0) {
    return {
        payload: {
            status: "error",
            message: "No updates provided"
        },
        topic: "todo/dashboard/editor"
    };
}

// Normalize field values before updating
const normalizedUpdates = {};

// Process each field with appropriate data type and validation
Object.keys(updates).forEach(key => {
    let value = updates[key];

    // Process each field based on its expected type
    switch (key) {
        case 'priority':
            // Ensure consistent casing for priority
            if (value) {
                normalizedUpdates.priority = value.toLowerCase();
            }
            break;

        case 'status':
            // Ensure consistent casing for status
            if (value) {
                normalizedUpdates.status = value.toLowerCase();
            }
            break;

        case 'duration_minutes':
            // Ensure duration is a number
            normalizedUpdates.duration_minutes = parseInt(value) || 0;
            break;

        case 'due_date':
            // Ensure date is in proper format if not empty
            if (value && value.trim() !== '') {
                normalizedUpdates.due_date = value;
            } else {
                normalizedUpdates.due_date = '';
            }
            break;

        case 'notes':
            // Ensure notes field is a string
            normalizedUpdates.notes = value || '';
            break;

        case 'enhanced_description':
            // Ensure enhanced_description field is a string
            normalizedUpdates.enhanced_description = value || '';
            break;

        case 'ticket':
            // Ensure ticket field is a string and normalize format
            if (value && value.trim() !== '') {
                // Remove any extra whitespace and normalize
                normalizedUpdates.ticket = value.trim();
            } else {
                normalizedUpdates.ticket = '';
            }
            break;

        default:
            // For other fields, just pass the value through
            normalizedUpdates[key] = value;
    }
});

// Add last_modified timestamp
normalizedUpdates.last_modified = Math.floor(Date.now() / 1000);

// If marking as completed, add completed_at timestamp
if (normalizedUpdates.status === 'completed') {
    normalizedUpdates.completed_at = Math.floor(Date.now() / 1000);
}

// Log normalized updates
node.warn("Normalized updates: " + JSON.stringify(normalizedUpdates));
node.warn("Original vs normalized case comparison:");
Object.keys(updates).forEach(key => {
    if (typeof updates[key] === 'string' && normalizedUpdates[key] !== updates[key]) {
        node.warn(`Field "${key}": "${updates[key]}" â†’ "${normalizedUpdates[key]}"`);
    }
});

// Format according to mongodb4 node documentation
// msg.payload must be an array of arguments for the driver operation
// For updateOne: first arg is query, second is update, third is options

// Prepare arguments as documented format
const query = { id: String(todoId) };
const update = { $set: normalizedUpdates };
const options = { upsert: false };

// Set operation parameters according to docs
msg.payload = [query, update, options];
msg.collection = "todos";
msg.operation = "updateOne";

// Add response routing topic to ensure result goes back to editor
msg.topic = "todo/action/update_multiple";

// Log the operation for debugging
node.warn("Sending MongoDB updateOne operation for todo ID: " + todoId);

// Return the message for further processing
return msg;

// Get the todo ID from the payload
const todoId = msg.payload.id;

if (!todoId)
{
    return { payload: { status: "error", message: "No todo ID provided" } };
}

// Check if the ID is a string and if it might be an ObjectId
let filterQuery = {};
if (todoId.length === 24 && /^[0-9a-fA-F]{24}$/.test(todoId))
{
    // This might be an ObjectId - set up filter to try both id and _id
    filterQuery = { $or: [{ id: todoId }, { _id: todoId }] };
} else
{
    // Just use id as a string
    filterQuery = { id: todoId };
}

// Format according to mongodb4 node documentation
// msg.payload must be an array of arguments for the driver operation
// For deleteOne: first arg is filter, second is options

// Set operation parameters according to docs
msg.payload = [filterQuery, {}]; // Filter and empty options
msg.collection = "todos";
msg.operation = "deleteOne";
msg.topic = "todo/action/delete"; // Keep the topic for routing

return msg;

// Get all the todos
const todos = msg.payload || [];

// Get active project filter from global context
const activeProjectFilter = global.get("projectFilter") || "all";

// Count priorities
const highPriority = todos.filter(todo => todo.priority === "high").length;
const mediumPriority = todos.filter(todo => todo.priority === "medium" || todo.priority === "initial").length;
const lowPriority = todos.filter(todo => todo.priority === "low").length;

// Sort pending todos by priority and creation date
const priorityRank = { "high": 0, "medium": 1, "low": 2, "initial": 1 };
todos.sort((a, b) =>
{
    // First by priority
    const priorityDiff = priorityRank[a.priority || "medium"] - priorityRank[b.priority || "medium"];
    if (priorityDiff !== 0) return priorityDiff;

    // Then by creation date (descending)
    return (b.created_at || 0) - (a.created_at || 0);
});

// Store pending todos in global context for other nodes to use
global.set("pendingTodos", todos);

// Get completed todos count (if available)
const completedTodos = global.get("completedTodos") || [];

// Format for dashboard
const dashboardData = {
    todos: todos,
    total_pending: todos.length,
    total_completed: completedTodos.length,
    high_priority: highPriority,
    medium_priority: mediumPriority,
    low_priority: lowPriority,
    active_project_filter: activeProjectFilter,
    last_updated: new Date().toISOString()
};

// Debug output
// node.warn("Publishing todos to dashboard: " + todos.length + " todos");
// node.warn("Priorities - High: " + highPriority + ", Medium: " + mediumPriority + ", Low: " + lowPriority);


return {
    payload: dashboardData,
    topic: "todo/dashboard/todos"
};

// Get completed todos from MongoDB
const completedTodos = msg.payload || [];

// Store in global context for other nodes to use
global.set("completedTodos", completedTodos);

// Sort completed todos by completion date (descending)
completedTodos.sort((a, b) => (b.completed_at || 0) - (a.completed_at || 0));

// Limit to most recent for dashboard
const recentCompleted = completedTodos.slice(0, 5);

// Find patterns in similar tasks (simplified implementation)
function findPatterns(todos) {
    const patterns = [];

    // Group by words in description
    const taskGroups = {};

    todos.forEach(todo => {
        const words = todo.description.toLowerCase().split(/\s+/).filter(w => w.length > 4);

        words.forEach(word => {
            if (!taskGroups[word]) {
                taskGroups[word] = [];
            }

            // Only add if not already in the group
            if (!taskGroups[word].find(t => t.id === todo.id)) {
                taskGroups[word].push(todo);
            }
        });
    });

    // Find patterns with at least 2 todos
    Object.keys(taskGroups).forEach(word => {
        if (taskGroups[word].length >= 2) {
            patterns.push({
                pattern_id: `pattern-${patterns.length + 1}`,
                keyword: word,
                similar_tasks: taskGroups[word].length,
                template: `${word} task`,
                automation_confidence: Math.round(60 + (taskGroups[word].length * 5)),
                examples: taskGroups[word].slice(0, 3).map(t => t.description)
            });
        }
    });

    return patterns.slice(0, 3); // Return top 3 patterns
}

// Generate simple recommendations
function generateRecommendations(todos) {
    const recommendations = [];

    // Find pending todos similar to completed todos
    const pendingTodos = global.get("pendingTodos") || [];

    // For demo, just recommend high priority for first few todos
    pendingTodos.slice(0, 2).forEach(todo => {
        if (todo.priority !== "high") {
            recommendations.push({
                todo_id: todo.id,
                description: todo.description,
                current_priority: todo.priority || "medium",
                recommended_priority: "high",
                confidence: Math.round(70 + Math.random() * 20)
            });
        }
    });

    return recommendations;
}

// Format AI suggestions for dashboard
const aiSuggestions = {
    automation_suggestions: findPatterns(completedTodos),
    priority_recommendations: generateRecommendations(completedTodos),
    pattern_analysis: {
        total_patterns: completedTodos.length > 0 ? Math.min(3, Math.floor(completedTodos.length / 2)) : 0,
        analyzed_todos: completedTodos.length
    },
    completed: recentCompleted
};

// Debug output
node.warn("Publishing AI suggestions with " + aiSuggestions.automation_suggestions.length + " automation suggestions");
node.warn("Publishing " + aiSuggestions.priority_recommendations.length + " priority recommendations");
node.warn("Including " + recentCompleted.length + " recently completed todos");

return { 
    payload: aiSuggestions,
    topic: "todo/dashboard/suggestions"
};

// Check if we found a todo
if (!msg.payload || msg.payload.length === 0) {
    return { payload: { status: "error", message: "Todo not found" } };
}

// Get the first todo from the results
const todo = msg.payload[0];

// Check which action we're performing
if (msg.topic === "todo/action/suggestions") {
    // Create a specific suggestion response for this todo
    const response = {
        todo: {
            id: todo.id,
            description: todo.description,
            current_priority: todo.priority || "medium",
            status: todo.status
        },
        suggestions: {
            suggested_priority: suggestPriority(todo),
            estimated_completion_time: estimateCompletionTime(todo),
            automation_confidence: Math.round(60 + Math.random() * 25)
        }
    };

    return {
        payload: response,
        topic: "todo/dashboard/suggestions"
    };
} else if (msg.topic.includes("schedule")) {
    // Get the date (either from the msg.date or default to tomorrow)
    const targetDate = msg.date || (() => {
        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate() + 1);
        return tomorrow.toISOString().split('T')[0];
    })();

    // Create a schedule specifically for this todo
    const scheduleResponse = createScheduleForTodo(todo, targetDate);

    return {
        payload: scheduleResponse,
        topic: "todo/dashboard/schedule"
    };
}

// Helper function to suggest priority
function suggestPriority(todo) {
    // Simple logic - but in real app would be more sophisticated
    const description = todo.description.toLowerCase();

    if (description.includes("urgent") || description.includes("important")) {
        return "high";
    }

    if (description.includes("review") || description.includes("meeting")) {
        return "medium";
    }

    return todo.priority || "medium";
}

// Helper function to estimate completion time
function estimateCompletionTime(todo) {
    // Simple logic - but in real app would analyze similar tasks
    const priority = todo.priority || "medium";

    // Estimate in minutes
    const estimates = {
        "high": 90,
        "medium": 60,
        "low": 45,
        "initial": 60
    };

    return estimates[priority];
}

// Create a schedule for a specific todo
function createScheduleForTodo(todo, dateStr) {
    // Create a Date object from the target date
    const date = new Date(dateStr);
    const weekday = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"][date.getDay()];

    // Define working hours based on the day of the week
    let workingHours;
    const day = date.getDay();

    if (day === 0) { // Sunday
        workingHours = { start: "10:00", end: "14:00" };
    } else if (day === 6) { // Saturday
        workingHours = { start: "10:00", end: "15:00" };
    } else { // Weekday
        workingHours = { start: "09:00", end: "17:00" };
    }

    // Calculate available minutes
    function timeToMinutes(timeStr) {
        const [hours, minutes] = timeStr.split(':').map(Number);
        return hours * 60 + minutes;
    }

    function minutesToTime(totalMinutes) {
        const hours = Math.floor(totalMinutes / 60);
        const minutes = totalMinutes % 60;
        return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
    }

    const startMinutes = timeToMinutes(workingHours.start);
    const endMinutes = timeToMinutes(workingHours.end);
    const availableMinutes = endMinutes - startMinutes;

    // Duration based on priority
    const durations = {
        "high": 90,
        "medium": 60,
        "low": 45,
        "initial": 60
    };

    const duration = durations[todo.priority || "medium"];

    // Calculate a good time slot based on priority
    let startTimeMinutes;
    if (todo.priority === "high") {
        // Schedule high priority in the morning
        startTimeMinutes = startMinutes + 60; // 1 hour after start
    } else if (todo.priority === "low") {
        // Schedule low priority in the afternoon
        startTimeMinutes = endMinutes - duration - 60; // 1 hour before end
    } else {
        // Schedule medium priority in mid-day
        startTimeMinutes = startMinutes + Math.floor((availableMinutes - duration) / 2);
    }

    // Ensure within bounds
    if (startTimeMinutes < startMinutes) {
        startTimeMinutes = startMinutes;
    }
    if (startTimeMinutes + duration > endMinutes) {
        startTimeMinutes = endMinutes - duration;
    }

    // Format times
    const startTimeStr = minutesToTime(startTimeMinutes);
    const endTimeStr = minutesToTime(startTimeMinutes + duration);

    // Create the schedule
    return {
        date: dateStr,
        weekday: weekday,
        working_hours: workingHours,
        schedule: [
            {
                todo_id: todo.id,
                description: todo.description,
                priority: todo.priority || "medium",
                start_time: startTimeStr,
                end_time: endTimeStr,
                duration_minutes: duration
            }
        ],
        total_tasks: 1,
        total_scheduled_minutes: duration,
        available_minutes: availableMinutes,
        utilization_percentage: Math.round((duration / availableMinutes) * 100)
    };
}

// Check if we found a todo
if (!msg.payload || msg.payload.length === 0)
{
    // Add more detailed error info
    const errorInfo = {
        status: "error",
        message: "Todo not found",
        payload_type: typeof msg.payload,
        payload_preview: msg.payload ? JSON.stringify(msg.payload).substring(0, 100) : "null"
    };

    return {
        payload: errorInfo,
        topic: "todo/dashboard/editor"
    };
}

// Get the first todo from the results
const todo = Array.isArray(msg.payload) ? msg.payload[0] : msg.payload;

// Handle MongoDB4 node returning different formats
let processedTodo = todo;
if (todo.result && Array.isArray(todo.result))
{
    processedTodo = todo.result[0];
} else if (todo.value)
{
    // MongoDB updateOne returnNewDocument response format
    processedTodo = todo.value;
}

// Check if this is a direct edit request from the "Copy ID" button
// In that case, avoid refreshing the todo list
const noRefresh = msg.noRefresh === true;
const directEdit = msg.directEdit === true;

// Debug information
node.warn("Processing edit request: noRefresh=" + noRefresh + ", directEdit=" + directEdit);

// Return the todo to send to the editor
let result = {
    payload: processedTodo,
    topic: "todo/dashboard/editor",
    // Pass along the flags
    noRefresh: noRefresh,
    directEdit: directEdit
};

return result;

// This function prepares a query for completed todos
// It also respects the global project filter

// Default parameters
let match = { status: "completed" }; // Only completed todos
let sort = { completed_at: -1 }; // Sort by completion date (newest first)
let limit = 100; // Only 5 most recent

// Get project filter from global context, if it exists
const projectFilter = global.get("projectFilter") || "all";
if (projectFilter !== "all")
{
    match.project = projectFilter;
    node.warn("Filtering completed todos by project: " + projectFilter);
}

// Prepare the aggregation pipeline
const pipeline = [
    // Match stage to filter todos
    { $match: match },

    // Sort todos by completion date
    { $sort: sort },

    // Limit to most recent completed todos
    { $limit: limit }
];

// Set the query parameters for MongoDB
// IMPORTANT: For MongoDB4 node, payload must be an array containing the pipeline
msg.payload = [pipeline];
msg.collection = "todos";
msg.operation = "aggregate";

node.warn("Completed todos pipeline prepared with " + pipeline.length + " stages");
return msg;

/**
 * Copy todo ID to clipboard
 * @param {Object} msg - The message object containing the todo data
 * @param {Object} node - The node object
 * @param {Function} send - Function to send messages
 * @return {Object} - Modified message object
 */
module.exports = function (msg, node, send)
{
    // This function will be called from the frontend
    // The actual copy functionality is implemented in the HTML/UI side
    // This just passes through the message for any additional processing

    // The todo data should be in msg.payload
    const todo = msg.payload;

    if (!todo || !todo._id)
    {
        node.error("No valid todo ID found");
        msg.payload = { error: "No valid todo ID found" };
        return msg;
    }

    // We just pass through the message since the clipboard copying
    // is handled client-side in the browser
    return msg;
};

// Get the todo ID from the payload
const todoId = msg.payload.id;

if (!todoId)
{
    return {
        payload: {
            status: "error",
            message: "No todo ID provided"
        },
        topic: "todo/dashboard/error"
    };
}

// Log the request for debugging
node.warn(`Getting AI insights for todo ID: ${todoId}`);

// First we need to fetch the todo from MongoDB to get its data
return {
    payload: {
        // Query for MongoDB4 node format
        filter: { id: todoId }
    },
    topic: "todo/action/get_todo_for_insights",
    collection: "todos",
    debug: {
        request_id: `ai_insights_${Date.now()}`,
        timestamp: new Date().toISOString(),
        todo_id: todoId
    }
};

// This function caches AI insights to reduce repeated API calls
// Uses Node-RED global context to store the cache

// Initialize the cache if it doesn't exist
const cache = global.get("aiInsightsCache") || {};

// Add a timestamp to the cache
const now = Date.now();

// Check if we're storing or retrieving from the cache
if (msg.topic === "todo/action/get_todo_for_insights")
{
    const todoId = msg.payload && msg.payload.filter && msg.payload.filter.id;

    if (!todoId)
    {
        // No ID to check cache, pass through
        return msg;
    }

    // Check if we have a recent cache entry (less than 30 minutes old)
    if (cache[todoId] &&
        cache[todoId].timestamp &&
        (now - cache[todoId].timestamp < 30 * 60 * 1000))
    {

        // We have a recent cache hit, return the cached insights
        return {
            payload: cache[todoId].data,
            topic: "todo/dashboard/insights",
            cached: true
        };
    }

    // No cache hit or cache expired, continue with API call
    return msg;
