// Clear all filters function replaced by central handler
return {
    topic: 'todo/action/clear_filters',
    payload: {
        clear_filters: true
    }
};

// This function serves as a central handler for all filter changes
// and ensures the todo list is refreshed when any filter is changed

// Get the current topic and payload
const topic = msg.topic || "";
const payload = msg.payload || {};

// Handle different filter types based on the topic
if (topic.includes("filter_project")) {
    // Handle project filter updates
    const project = payload.project || "all";
    global.set("projectFilter", project);
    node.warn("Project filter set to: " + project);
}
else if (topic.includes("filter_status")) {
    // Handle status filter updates
    const status = payload.status || "all";
    global.set("statusFilter", status);
    node.warn("Status filter set to: " + status);
}
else if (topic.includes("filter_priority")) {
    // Handle priority filter updates
    const priority = payload.priority || "all";
    global.set("priorityFilter", priority);
    node.warn("Priority filter set to: " + priority);
}
else if (topic.includes("filter_date")) {
    // Handle date filter updates
    const date = payload.date || null;
    global.set("dateFilter", date);
    node.warn("Date filter set to: " + (date ? new Date(date).toISOString().split('T')[0] : "none"));
}
else if (topic.includes("clear_filters")) {
    // Handle clearing all filters
    global.set("projectFilter", "all");
    global.set("statusFilter", "all");
    global.set("priorityFilter", "all");
    global.set("dateFilter", null);
    node.warn("All filters cleared");
}

// Publish a status update
const activeFilters = [];
if (global.get("projectFilter") !== "all") activeFilters.push("Project: " + global.get("projectFilter"));
if (global.get("statusFilter") !== "all") activeFilters.push("Status: " + global.get("statusFilter"));
if (global.get("priorityFilter") !== "all") activeFilters.push("Priority: " + global.get("priorityFilter"));
if (global.get("dateFilter")) activeFilters.push("Date: " + new Date(global.get("dateFilter")).toLocaleDateString());

// Create a status message
let statusMsg = { payload: activeFilters.length > 0 ? 
    "Filtered by " + activeFilters.join(", ") : 
    "Showing all items" };

// Return messages
return [
    // Output 1: Message to trigger todo list refresh
    {
        topic: "todo/action/refresh_todos",
        payload: {
            refreshType: "filter_change",
            filters: {
                project: global.get("projectFilter"),
                status: global.get("statusFilter"),
                priority: global.get("priorityFilter"),
                date: global.get("dateFilter")
            }
        }
    },
    // Output 2: Status update for display
    statusMsg
];

// Format the response for the HTTP API

// Get filter information from the message
const filters = msg._filters || {};

// Prepare response metadata
const meta = {
    count: msg.payload.length,
    filters: filters,
    timestamp: new Date().toISOString()
};

// Filter names for display
const filterNames = [];
if (filters.project !== "all") filterNames.push(`Project: ${filters.project}`);
if (filters.status !== "all") filterNames.push(`Status: ${filters.status}`);
if (filters.priority !== "all") filterNames.push(`Priority: ${filters.priority}`);
if (filters.date) filterNames.push(`Date: ${filters.date}`);

// Format filter text for display
const filterText = filterNames.length > 0 ?
    `Filtered by ${filterNames.join(", ")}` :
    "Showing all items";

// Return messages for HTTP response and MQTT status
return [
    // First output: HTTP response
    {
        payload: {
            data: msg.payload,
            meta: meta
        }
    },
    // Second output: MQTT status message
    {
        payload: filterText,
        topic: "todo/sync/status"
    }
];

// Process incoming log messages
// This function now processes messages either from the API or direct calls

// If the message already has a topic set for routing, preserve it
if (msg.topic && (msg.topic.startsWith('todo/log/') || msg.topic === 'startup')) {
    // Keep the existing topic
    // This ensures API messages keep their original routing
    return msg;
}

// For messages without a specific topic (like direct log entries)
if (msg.payload) {
    // Set the topic for routing to display in UI
    msg.topic = "todo/log/new_entry";

    // The payload could be from API or MQTT
    // Make sure timestamp is properly parsed
    if (typeof msg.payload.timestamp === 'string') {
        // Create a Date object from the ISO string
        const timestamp = new Date(msg.payload.timestamp);
        msg.payload.timestamp = timestamp;
    }

    return msg;
} else {
    return null;
}

// This function handles requests for todo logs from the UI
// It calls the Omnispindle API to get logs instead of querying MongoDB directly

// Debug function to help troubleshoot
function debugObject(obj, prefix = '') {
    if (!obj) return 'null';
    if (typeof obj === 'object') {
        return prefix + JSON.stringify(obj, null, 2);
    }
    return prefix + obj;
}

if (msg.payload && msg.payload.action === 'get_logs') {
    // Extract request parameters
    const page = msg.payload.page || 1;
    const pageSize = msg.payload.pageSize || 20;
    const filter = msg.payload.filter || 'all';
    const project = msg.payload.project || 'all';
    const append = msg.payload.append || false;

    // Try direct MongoDB query as a fallback
    // This is more reliable when running on the same machine as MongoDB
    const collection = 'todo_logs';
    const mongoQuery = {};

    // Apply operation filter if specified
    if (filter !== 'all') {
        mongoQuery.operation = filter;
    }

    // Apply project filter if specified
    if (project !== 'all') {
        mongoQuery.project = project;
    }

    node.status({ fill: "blue", shape: "dot", text: "Querying MongoDB directly" });
    node.warn("Querying MongoDB collection: " + collection);

    // Create MongoDB query message
    return [{
        topic: 'todo/log/mongodb_query',
        collection: collection,
        operation: 'find',
        payload: mongoQuery,
        limit: pageSize,
        skip: (page - 1) * pageSize,
        sort: { timestamp: -1 },
        projection: {},
        append: append
    }];
}

// If it's a response from MongoDB
if (msg.topic === 'todo/log/mongodb_response' || 
    (msg.collection === 'todo_logs' && Array.isArray(msg.payload))) {
    try {
        const logEntries = msg.payload;
        const append = msg.append || false;

        // Debug information
        node.status({ fill: "green", shape: "dot", text: "Found " + logEntries.length + " log entries" });
        node.warn("MongoDB query returned " + logEntries.length + " results");

        // Get unique projects for filtering
        const projects = [...new Set(logEntries.map(entry => entry.project).filter(Boolean))];

        // Format the MongoDB response for the dashboard
        return [{
            topic: 'todo/log/response',
            payload: {
                logEntries: logEntries,
                totalCount: logEntries.length,  // This is an approximation
                page: msg.skip ? (msg.skip / msg.limit) + 1 : 1,
                pageSize: msg.limit || 20,
                hasMore: logEntries.length >= (msg.limit || 20),  // Approximation
                projects: projects,
                append: append
            }
        }];
    } catch (err) {
        // Error parsing the response
        node.error("Exception processing MongoDB response: " + err.message);
        return [{
            topic: 'todo/log/response',
            payload: {
                error: 'Error processing MongoDB response: ' + err.message,
                logEntries: [],
                debug: debugObject(msg.payload)
            }
        }];
    }
}

// If it's a response from the API, format it for the UI
if (msg.topic === 'todo/log/api_response' || msg.statusCode) {
    try {
        const response = msg.payload;
        const append = msg.append || false;

        // Debug information
        node.status({ fill: "blue", shape: "dot", text: "Received API response" });

        // Accept any response format that contains log entries
        // This makes the code more flexible with different API response structures
        let logEntries = [];
        let totalCount = 0;
        let page = 1;
        let pageSize = 20;
        let hasMore = false;
        let projects = [];

        // Try to extract data from various possible response formats
        if (response.data && response.data.logs) {
            // Format 1: {data: {logs: [...], ...}}
            logEntries = response.data.logs;
            totalCount = response.data.totalCount || response.data.count || logEntries.length;
            page = response.data.page || 1;
            pageSize = response.data.pageSize || 20;
            hasMore = response.data.hasMore || false;
            projects = response.data.projects || [];
        } else if (response.data && response.data.logEntries) {
            // Format 2: {data: {logEntries: [...], ...}}
            logEntries = response.data.logEntries;
            totalCount = response.data.totalCount || response.data.count || logEntries.length;
            page = response.data.page || 1;
            pageSize = response.data.pageSize || 20;
            hasMore = response.data.hasMore || false;
            projects = response.data.projects || [];
        } else if (response.logs) {
            // Format 3: {logs: [...], ...}
            logEntries = response.logs;
            totalCount = response.totalCount || response.count || logEntries.length;
            page = response.page || 1;
            pageSize = response.pageSize || 20;
            hasMore = response.hasMore || false;
            projects = response.projects || [];
        } else if (response.logEntries) {
            // Format 4: {logEntries: [...], ...}
            logEntries = response.logEntries;
            totalCount = response.totalCount || response.count || logEntries.length;
            page = response.page || 1;
            pageSize = response.pageSize || 20;
            hasMore = response.hasMore || false;
            projects = response.projects || [];
        } else if (Array.isArray(response)) {
            // Format 5: Direct array of logs
            logEntries = response;
            totalCount = logEntries.length;
            hasMore = false;
        } else if (response.items && Array.isArray(response.items)) {
            // Format 6: {items: [...], count: X}
            logEntries = response.items;
            totalCount = response.count || logEntries.length;
            hasMore = logEntries.length < totalCount;
        }

        // Format the response for the dashboard
        return [{
            topic: 'todo/log/response',
            payload: {
                logEntries: logEntries,
                totalCount: totalCount,
                page: page,
                pageSize: pageSize,
                hasMore: hasMore,
                projects: projects,
                append: append
            }
        }];
    } catch (err) {
        // Error parsing the response
        node.error("Exception processing API response: " + err.message + "\nPayload: " + debugObject(msg.payload));
        return [{
            topic: 'todo/log/response',
            payload: {
                error: 'Error processing API response: ' + err.message,
                logEntries: [],
                debug: debugObject(msg.payload)
            }
        }];
    }
}

// Initial data load - trigger an API request automatically on startup
// This ensures logs are loaded without waiting for user interaction
if (msg.topic === 'startup') {
    return [{
        topic: 'todo/log/request',
        payload: {
            action: 'get_logs',
            page: 1,
            pageSize: 20,
            filter: 'all',
            project: 'all'
        }
    }];
}

// Pass through other messages
return null;

initialize:
