// Process incoming log messages
// This function now processes messages either from the API or direct calls

// If the message already has a topic set for routing, preserve it
if (msg.topic && (msg.topic.startsWith('todo/log/') || msg.topic === 'startup')) {
    // Keep the existing topic
    // This ensures API messages keep their original routing
    return msg;
}

// For messages without a specific topic (like direct log entries)
if (msg.payload) {
    // Set the topic for routing to display in UI
    msg.topic = "todo/log/new_entry";

    // The payload could be from API or MQTT
    // Make sure timestamp is properly parsed
    if (typeof msg.payload.timestamp === 'string') {
        // Create a Date object from the ISO string
        const timestamp = new Date(msg.payload.timestamp);
        msg.payload.timestamp = timestamp;
    }

    return msg;
} else {
    return null;
}

// Process API or MongoDB response
// Add debug info for tracing responses

// Check if this is a MongoDB response
if (msg.collection === 'todo_logs' && Array.isArray(msg.payload)) {
    node.warn("Received MongoDB response with " + msg.payload.length + " entries");
    msg.topic = 'todo/log/mongodb_response';
    // Copy all the MongoDB query parameters
    msg.limit = msg.limit || 20;
    msg.skip = msg.skip || 0;
    return msg;
}

// Handle API response
node.status({ fill: "blue", shape: "dot", text: "Response received" });

// Set topic for consistent routing
msg.topic = 'todo/log/response';

// Preserve the append flag
msg.append = msg.append || false;

// Debug response in logs
if (msg.statusCode >= 400) {
    node.error("HTTP Error: " + msg.statusCode + ", response: " + JSON.stringify(msg.payload));
    // Return error message
    return [{
        topic: 'todo/log/response',
        payload: {
            error: 'HTTP Error: ' + msg.statusCode,
            logEntries: [],
            debug: JSON.stringify(msg.payload)
        }
    }];
}

node.warn("Response processed successfully");

return msg;

// Handle tab selection to refresh logs when tab is shown
if (msg.payload && msg.payload.tab === "t-todo-logs") {
    // Tab was selected, refresh logs if needed
    return {
        topic: "todo/log/refresh",
        payload: {}
    };
}

// Pass through other messages
return msg;
